    //================== INSTALLATION ==================//

    private void startInstallation() {
        new Thread(new Runnable() {
            @Override
            public void run() {
                boolean success = copyObbFile();
                final boolean finalSuccess = success;

                mainHandler.post(new Runnable() {
                    @Override
                    public void run() {
                        onInstallComplete(finalSuccess);
                    }
                });
            }
        }).start();
    }

    private boolean copyObbFile() {
        InputStream is = null;
        OutputStream os = null;

        try {
            File obbDir = getObbDir();
            if (!obbDir.exists()) {
                obbDir.mkdirs();
            }

            File targetFile = new File(obbDir, OBB_FILE_NAME);

            long totalSize = 0;
            try {
                AssetFileDescriptor afd = getAssets().openFd(OBB_FILE_NAME);
                totalSize = afd.getLength();
                afd.close();
            } catch (Exception e) {
                // ignore
            }

            // Zaten yüklüyse skip
            if (targetFile.exists() && totalSize > 0 && targetFile.length() == totalSize) {
                updateUI(100, "Already installed!", ICON_SUCCESS, STATE_ON);
                return true;
            }

            if (targetFile.exists()) {
                targetFile.delete();
            }

            updateUI(0, "Opening file...", ICON_FOLDER, ACCENT_BLUE);

            is = getAssets().open(OBB_FILE_NAME);
            os = new FileOutputStream(targetFile);

            if (totalSize == 0) {
                totalSize = is.available();
            }

            byte[] buffer = new byte[16384];
            long copied = 0;
            int length;
            int lastPercent = -1;

            while ((length = is.read(buffer)) > 0) {
                os.write(buffer, 0, length);
                copied += length;

                int percent = totalSize > 0 ? (int) ((copied * 100) / totalSize) : 0;
                if (percent != lastPercent) {
                    lastPercent = percent;
                    final String status = formatSize(copied) + " / " + formatSize(totalSize);
                    updateUI(percent, status, ICON_DOWNLOAD, ACCENT_BLUE);
                }
            }

            os.flush();
            updateUI(100, "Complete!", ICON_SUCCESS, STATE_ON);
            return true;

        } catch (Exception e) {
            e.printStackTrace();
            updateUI(-1, "Error: " + e.getMessage(), ICON_ERROR, STATE_OFF);
            return false;
        } finally {
            try {
                if (is != null) is.close();
                if (os != null) os.close();
            } catch (Exception e) {
                // ignore
            }
        }
    }

    private void updateUI(final int percent, final String status, final String icon, final int iconColor) {
        mainHandler.post(new Runnable() {
            @Override
            public void run() {
                if (percent >= 0) {
                    percentText.setText(percent + "%");
                    updateProgressBar(percent);
                }
                if (status != null) {
                    statusText.setText(status);
                }
                if (icon != null) {
                    statusIcon.setText(icon);
                    statusIcon.setTextColor(iconColor);
                }
            }
        });
    }

    private void updateProgressBar(final int percent) {
        progressFrame.post(new Runnable() {
            @Override
            public void run() {
                int parentWidth = progressFrame.getWidth();
                if (parentWidth <= 0) return;

                int targetWidth = (int) (parentWidth * (percent / 100.0f));
                FrameLayout.LayoutParams params = (FrameLayout.LayoutParams) progressFill.getLayoutParams();
                params.width = targetWidth;
                progressFill.setLayoutParams(params);
            }
        });
    }

    private void onInstallComplete(boolean success) {
        if (success) {
            // Success state
            statusIcon.setText(ICON_SUCCESS);
            statusIcon.setTextColor(STATE_ON);
            statusText.setText("Installation complete!");
            statusText.setTextColor(STATE_ON);

            percentText.setText(ICON_CHECK);
            percentText.setTextColor(STATE_ON);
            percentText.setTypeface(iconFont);
            percentText.setTextSize(48f);

            // Progress bar green
            GradientDrawable fillDrawable = new GradientDrawable();
            fillDrawable.setCornerRadius(dp(6));
            fillDrawable.setColor(STATE_ON);
            progressFill.setBackground(fillDrawable);

            // Animate and go to main
            mainHandler.postDelayed(new Runnable() {
                @Override
                public void run() {
                    mainCard.animate()
                        .alpha(0f)
                        .scaleX(0.9f)
                        .scaleY(0.9f)
                        .setDuration(200)
                        .withEndAction(new Runnable() {
                            @Override
                            public void run() {
                                Main.Start(ObbInstallerActivity.this);
                                finish();
                            }
                        })
                        .start();
                }
            }, 1200);

        } else {
            // Error state
            statusIcon.setText(ICON_ERROR);
            statusIcon.setTextColor(STATE_OFF);
            statusText.setText("Installation failed!");
            statusText.setTextColor(STATE_OFF);

            percentText.setText(ICON_CLOSE);
            percentText.setTextColor(STATE_OFF);
            percentText.setTypeface(iconFont);
            percentText.setTextSize(48f);

            // Progress bar red
            GradientDrawable fillDrawable = new GradientDrawable();
            fillDrawable.setCornerRadius(dp(6));
            fillDrawable.setColor(STATE_OFF);
            progressFill.setBackground(fillDrawable);

            mainHandler.postDelayed(new Runnable() {
                @Override
                public void run() {
                    finish();
                }
            }, 3000);
        }
    }

    //================== HELPERS ==================//

    private String formatSize(long bytes) {
        if (bytes >= 1024L * 1024L * 1024L) {
            return String.format("%.2f GB", bytes / (1024.0 * 1024.0 * 1024.0));
        } else if (bytes >= 1024L * 1024L) {
            return String.format("%.1f MB", bytes / (1024.0 * 1024.0));
        } else if (bytes >= 1024L) {
            return String.format("%.1f KB", bytes / 1024.0);
        }
        return bytes + " B";
    }

    private int dp(int value) {
        return (int) TypedValue.applyDimension(
            TypedValue.COMPLEX_UNIT_DIP, value,
            getResources().getDisplayMetrics()
        );
    }

